export function withWasm(wasmBridge, fn, withRetptr = true) {
  return (...params) => {
    const wasm = wasmBridge.wasm;
    if (!wasm) {
      throw new Error('The WASM instance has not been initialized. Ensure that you wait for the initialization Promise with initCrypto() from @zcloak/crypto.');
    }
    let retptr = 0;
    if (withRetptr) {
      retptr = wasm.__wbindgen_add_to_stack_pointer(-16);
    }
    try {
      const result = fn([wasm, retptr], ...params);
      return result;
    } finally {
      if (withRetptr) {
        wasm.__wbindgen_add_to_stack_pointer(16);
      }
    }
  };
}